mapper.select:
SELECT activity_id, paType, sender_id, receiver_id, project_id, paMessage, paStatus, paIsRead, paCreatedAt, paUpdatedAt
FROM projectActivity
WHERE activity_id = #{activityId}

SELECT activity_id, paType, sender_id, receiver_id, project_id, paMessage, paStatus, paIsRead, paCreatedAt, paUpdatedAt
FROM projectActivity
WHERE receiver_id = #{value}

mapper.update:
UPDATE projectActivity 
SET paStatus = #{paStatus},
WHERE activity_id = #{activityId}

UPDATE projectActivity 
SET paStatus = 'READ',
WHERE activity_id = #{activityId}
