mapper.select:
select count(*) 
from userprojectrole 
where project_id=#{projectId}

select uprrole
from userprojectrole
where project_id = #{projectId} AND user_id = #{userId}

select uprRole
from userprojectrole
where user_id=#{userId} and project_id=#{projectId}

mapper.update:
UPDATE userProjectRole 
SET uprRole = #{uprRole}
WHERE project_id = #{projectId} 
  AND user_id = #{userId}
